definition.script:
pipeline {
    agent any
    tools {
        jdk 'jdk17'
        maven 'maven3'
    }

    stages {   
         stage('Git Checkout') {
            steps {
              git branch: 'main', url: 'https://github.com/KishorKumarParoi/Boardgame.git'
            }
        }
        stage('Compile') {
            steps {
            sh 'mvn compile'
            }
        }

        stage('Test') {
            steps {
                sh 'mvn test'
            }
        }

        stage('Package') {
            steps {
                sh 'mvn clean package'
            }
        }

         stage('deploy') {
            steps {
                deploy adapters: [tomcat9(alternativeDeploymentContext: '', credentialsId: 'tomcat-server', path: '', url: 'http://54.164.49.90:5000/')], contextPath: 'Planview', war: 'target/*.war'
            }
        }

         stage('Hello') {
            steps {
                echo "Hello World!"
            }
        }

    }
}
